(5, 1, '2021-10-02 21:24:52', '2021-10-02 12:24:52', '<!-- wp:paragraph -->\n<p>テストページです。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>ローカルから本番に移行できるかどうかの確認</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->', 'テスト', '', 'publish', 'open', 'open', '', 'test', '', '', '2021-10-02 21:25:33', '2021-10-02 12:25:33', '', 0, 'http://localhost:8888/?p=5', 0, 'post', '', 0),
(6, 1, '2021-10-02 21:24:52', '2021-10-02 12:24:52', '<!-- wp:paragraph -->\n<p>テストページです。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>ローカルから本番に移行できるかどうかの確認</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->', 'テスト', '', 'inherit', 'closed', 'closed', '', '5-revision-v1', '', '', '2021-10-02 21:24:52', '2021-10-02 12:24:52', '', 5, 'http://localhost:8888/?p=6', 0, 'revision', '', 0);

