--
-- テーブルの構造 `wp_posts`
--

CREATE TABLE `wp_posts` (
  `ID` bigint(20) UNSIGNED NOT NULL,
  `post_author` bigint(20) UNSIGNED NOT NULL DEFAULT '0',
  `post_date` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_date_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content` longtext COLLATE utf8mb4_unicode_520_ci NOT NULL,
  `post_title` text COLLATE utf8mb4_unicode_520_ci NOT NULL,
  `post_excerpt` text COLLATE utf8mb4_unicode_520_ci NOT NULL,
  `post_status` varchar(20) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT 'publish',
  `comment_status` varchar(20) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT 'open',
  `ping_status` varchar(20) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT 'open',
  `post_password` varchar(255) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT '',
  `post_name` varchar(200) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT '',
  `to_ping` text COLLATE utf8mb4_unicode_520_ci NOT NULL,
  `pinged` text COLLATE utf8mb4_unicode_520_ci NOT NULL,
  `post_modified` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_modified_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content_filtered` longtext COLLATE utf8mb4_unicode_520_ci NOT NULL,
  `post_parent` bigint(20) UNSIGNED NOT NULL DEFAULT '0',
  `guid` varchar(255) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT '',
  `menu_order` int(11) NOT NULL DEFAULT '0',
  `post_type` varchar(20) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT 'post',
  `post_mime_type` varchar(100) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT '',
  `comment_count` bigint(20) NOT NULL DEFAULT '0'
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_520_ci;

--
-- テーブルのデータのダンプ `wp_posts`
--

INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2021-10-02 21:21:02', '2021-10-02 12:21:02', '<!-- wp:paragraph -->\n<p>WordPress へようこそ。こちらは最初の投稿です。編集または削除し、コンテンツ作成を始めてください。</p>\n<!-- /wp:paragraph -->', 'Hello world!', '', 'publish', 'open', 'open', '', 'hello-world', '', '', '2021-10-02 21:21:02', '2021-10-02 12:21:02', '', 0, 'http://localhost:8888/?p=1', 0, 'post', '', 1),
(2, 1, '2021-10-02 21:21:02', '2021-10-02 12:21:02', '<!-- wp:paragraph -->\n<p>これはサンプルページです。同じ位置に固定され、(多くのテーマでは) サイトナビゲーションメニューに含まれる点がブログ投稿とは異なります。まずは、サイト訪問者に対して自分のことを説明する自己紹介ページを作成するのが一般的です。たとえば以下のようなものです。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:quote -->\n<blockquote class=\"wp-block-quote\"><p>はじめまして。昼間はバイク便のメッセンジャーとして働いていますが、俳優志望でもあります。これは僕のサイトです。ロサンゼルスに住み、ジャックという名前のかわいい犬を飼っています。好きなものはピニャコラーダ、そして通り雨に濡れること。</p></blockquote>\n<!-- /wp:quote -->\n\n<!-- wp:paragraph -->\n<p>または、このようなものです。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:quote -->\n<blockquote class=\"wp-block-quote\"><p>XYZ 小道具株式会社は1971年の創立以来、高品質の小道具を皆様にご提供させていただいています。ゴッサム・シティに所在する当社では2,000名以上の社員が働いており、様々な形で地域のコミュニティへ貢献しています。</p></blockquote>\n<!-- /wp:quote -->\n\n<!-- wp:paragraph -->\n<p>新しく WordPress ユーザーになった方は、<a href=\"http://localhost:8888/wp-admin/\">ダッシュボード</a>へ行ってこのページを削除し、独自のコンテンツを含む新しいページを作成してください。それでは、お楽しみください !</p>\n<!-- /wp:paragraph -->', 'サンプルページ', '', 'publish', 'closed', 'open', '', 'sample-page', '', '', '2021-10-02 21:21:02', '2021-10-02 12:21:02', '', 0, 'http://localhost:8888/?page_id=2', 0, 'page', '', 0),
(3, 1, '2021-10-02 21:21:02', '2021-10-02 12:21:02', '<!-- wp:heading --><h2>私たちについて</h2><!-- /wp:heading --><!-- wp:paragraph --><p><strong class=\"privacy-policy-tutorial\">提案テキスト: </strong>私たちのサイトアドレスは http://localhost:8888 です。</p><!-- /wp:paragraph --><!-- wp:heading --><h2>コメント</h2><!-- /wp:heading --><!-- wp:paragraph --><p><strong class=\"privacy-policy-tutorial\">提案テキスト: </strong>訪問者がこのサイトにコメントを残す際、コメントフォームに表示されているデータ、そしてスパム検出に役立てるための IP アドレスとブラウザーユーザーエージェント文字列を収集します。</p><!-- /wp:paragraph --><!-- wp:paragraph --><p>メールアドレスから作成される匿名化された (「ハッシュ」とも呼ばれる) 文字列は、あなたが Gravatar サービスを使用中かどうか確認するため同サービスに提供されることがあります。同サービスのプライバシーポリシーは https://automattic.com/privacy/ にあります。コメントが承認されると、プロフィール画像がコメントとともに一般公開されます。</p><!-- /wp:paragraph --><!-- wp:heading --><h2>メディア</h2><!-- /wp:heading --><!-- wp:paragraph --><p><strong class=\"privacy-policy-tutorial\">提案テキスト: </strong>サイトに画像をアップロードする際、位置情報 (EXIF GPS) を含む画像をアップロードするべきではありません。サイトの訪問者は、サイトから画像をダウンロードして位置データを抽出することができます。</p><!-- /wp:paragraph --><!-- wp:heading --><h2>Cookie</h2><!-- /wp:heading --><!-- wp:paragraph --><p><strong class=\"privacy-policy-tutorial\">提案テキスト: </strong>サイトにコメントを残す際、お名前、メールアドレス、サイトを Cookie に保存することにオプトインできます。これはあなたの便宜のためであり、他のコメントを残す際に詳細情報を再入力する手間を省きます。この Cookie は1年間保持されます。</p><!-- /wp:paragraph --><!-- wp:paragraph --><p>ログインページを訪問すると、お使いのブラウザーが Cookie を受け入れられるかを判断するために一時 Cookie を設定します。この Cookie は個人データを含んでおらず、ブラウザーを閉じると廃棄されます。</p><!-- /wp:paragraph --><!-- wp:paragraph --><p>ログインの際さらに、ログイン情報と画面表示情報を保持するため、私たちはいくつかの Cookie を設定します。ログイン Cookie は2日間、画面表示オプション Cookie は1年間保持されます。「ログイン状態を保存する」を選択した場合、ログイン情報は2週間維持されます。ログアウトするとログイン Cookie は消去されます。</p><!-- /wp:paragraph --><!-- wp:paragraph --><p>もし投稿を編集または公開すると、さらなる Cookie がブラウザーに保存されます。この Cookie は個人データを含まず、単に変更した投稿の ID を示すものです。1日で有効期限が切れます。</p><!-- /wp:paragraph --><!-- wp:heading --><h2>他サイトからの埋め込みコンテンツ</h2><!-- /wp:heading --><!-- wp:paragraph --><p><strong class=\"privacy-policy-tutorial\">提案テキスト: </strong>このサイトの投稿には埋め込みコンテンツ (動画、画像、投稿など) が含まれます。他サイトからの埋め込みコンテンツは、訪問者がそのサイトを訪れた場合とまったく同じように振る舞います。</p><!-- /wp:paragraph --><!-- wp:paragraph --><p>これらのサイトは、あなたのデータの収集、Cookie の使用、サードパーティによる追加トラッキングの埋め込み、埋め込みコンテンツとのやりとりの監視を行うことがあります。アカウントを使ってそのサイトにログイン中の場合、埋め込みコンテンツとのやりとりのトラッキングも含まれます。</p><!-- /wp:paragraph --><!-- wp:heading --><h2>あなたのデータの共有先</h2><!-- /wp:heading --><!-- wp:paragraph --><p><strong class=\"privacy-policy-tutorial\">提案テキスト: </strong>パスワードリセットをリクエストすると、IP アドレスがリセット用のメールに含まれます。</p><!-- /wp:paragraph --><!-- wp:heading --><h2>データを保存する期間</h2><!-- /wp:heading --><!-- wp:paragraph --><p><strong class=\"privacy-policy-tutorial\">提案テキスト: </strong>あなたがコメントを残すと、コメントとそのメタデータが無期限に保持されます。これは、モデレーションキューにコメントを保持しておく代わりに、フォローアップのコメントを自動的に認識し承認できるようにするためです。</p><!-- /wp:paragraph --><!-- wp:paragraph --><p>このサイトに登録したユーザーがいる場合、その方がユーザープロフィールページで提供した個人情報を保存します。すべてのユーザーは自分の個人情報を表示、編集、削除することができます (ただしユーザー名は変更することができません)。サイト管理者もそれらの情報を表示、編集できます。</p><!-- /wp:paragraph --><!-- wp:heading --><h2>データに対するあなたの権利</h2><!-- /wp:heading --><!-- wp:paragraph --><p><strong class=\"privacy-policy-tutorial\">提案テキスト: </strong>このサイトのアカウントを持っているか、サイトにコメントを残したことがある場合、私たちが保持するあなたについての個人データ (提供したすべてのデータを含む) をエクスポートファイルとして受け取るリクエストを行うことができます。また、個人データの消去リクエストを行うこともできます。これには、管理、法律、セキュリティ目的のために保持する義務があるデータは含まれません。</p><!-- /wp:paragraph --><!-- wp:heading --><h2>あなたのデータの送信先</h2><!-- /wp:heading --><!-- wp:paragraph --><p><strong class=\"privacy-policy-tutorial\">提案テキスト: </strong>訪問者によるコメントは、自動スパム検出サービスを通じて確認を行う場合があります。</p><!-- /wp:paragraph -->', 'プライバシーポリシー', '', 'draft', 'closed', 'open', '', 'privacy-policy', '', '', '2021-10-02 21:21:02', '2021-10-02 12:21:02', '', 0, 'http://localhost:8888/?page_id=3', 0, 'page', '', 0),
(4, 1, '2021-10-02 21:21:12', '0000-00-00 00:00:00', '', '自動下書き', '', 'auto-draft', 'open', 'open', '', '', '', '', '2021-10-02 21:21:12', '0000-00-00 00:00:00', '', 0, 'http://localhost:8888/?p=4', 0, 'post', '', 0),
(5, 1, '2021-10-02 21:24:52', '2021-10-02 12:24:52', '<!-- wp:html -->\n[toc]\n<div class=\"u-margin__bottom--xxxlarge\">\n  <h2 class=\"c-headingSecond u-margin__bottom--large\">正しく目次は出ているでしょうか</h2>\n  <p>出ていました！</p>\n</div>\n\n<div class=\"u-margin__bottom--xxxlarge\">\n  <h2 class=\"c-headingSecond u-margin__bottom--large\">香水の種類について</h2>\n  <p>種類は主に以下の4つがあります。</p>\n  <ul class=\"c-list u-margin__bottom--large\">\n    <li>パルファム</li>\n    <li>オーデパルファム</li>\n    <li>オードトワレ</li>\n    <li>オーデコロン</li>\n  </ul>\n</div>\n\n<div class=\"u-margin__bottom--xxxlarge\">\n  <h2 class=\"c-headingSecond u-margin__bottom--large\">香水の構成について</h2>\n  <p>香水は以下の3つで構成されています。</p>\n  <ul class=\"c-list u-margin__bottom--large\">\n    <li>トップ</li>\n    <li>ミドル</li>\n    <li>ラスト</li>\n  </ul>\n  <div class=\"u-margin__bottom--xxxlarge\">\n    <h3 class=\"c-headingThird u-margin__bottom--large\">トップ</h3>\n    <p>最初に香るもの</p>\n  </div>\n  <div class=\"u-margin__bottom--xxxlarge\">\n    <h3 class=\"c-headingThird u-margin__bottom--large\">ミドル</h3>\n    <p>次に香るもの</p>\n  </div>\n  <div class=\"u-margin__bottom--xxxlarge\">\n    <h3 class=\"c-headingThird u-margin__bottom--large\">ラスト</h3>\n    <p>最後に香るもの</p>\n  </div>\n</div>\n\n<div class=\"u-margin__bottom--xxxlarge\">\n  <h2 class=\"c-headingSecond u-margin__bottom--large\">香水のつける場所</h2>\n  <p>手首や首などがいいね</p>\n</div>\n<!-- /wp:html -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->', '香水についてテスト記事', '', 'publish', 'open', 'open', '', 'test', '', '', '2021-10-07 16:08:10', '2021-10-07 07:08:10', '', 0, 'http://localhost:8888/?p=5', 0, 'post', '', 0),
(6, 1, '2021-10-02 21:24:52', '2021-10-02 12:24:52', '<!-- wp:paragraph -->\n<p>テストページです。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>ローカルから本番に移行できるかどうかの確認</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->', 'テスト', '', 'inherit', 'closed', 'closed', '', '5-revision-v1', '', '', '2021-10-02 21:24:52', '2021-10-02 12:24:52', '', 5, 'http://localhost:8888/?p=6', 0, 'revision', '', 0),
(8, 1, '2021-10-05 22:01:20', '2021-10-05 13:01:20', '<!-- wp:paragraph -->\n<p>テストページです。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>ローカルから本番に移行できるかどうかの確認</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:shortcode -->\ntoc\n<!-- /wp:shortcode -->\n\n<!-- wp:html -->\n<h2 class=\"c-headingSecond u-margin__bottom--large\">正しく目次は出ているでしょうか</h2>\n<h2 class=\"c-headingSecond u-margin__bottom--large\">香水の種類について</h2>\n<ul class=\"c-list u-margin__bottom--large\">\n  <li>パルファム</li>\n  <li>オーデパルファム</li>\n  <li>オードトワレ</li>\n  <li>オーデコロン</li>\n</ul>\n<h2 class=\"c-headingSecond u-margin__bottom--large\">香水の構成について</h2>\n<ul class=\"c-list u-margin__bottom--large\">\n  <li>トップ</li>\n  <li>ミドル</li>\n  <li>ラスト</li>\n</ul>\n<h3 class=\"c-headingThird u-margin__bottom--large\">トップ</h3>\n<h3 class=\"c-headingThird u-margin__bottom--large\">ミドル</h3>\n<h3 class=\"c-headingThird u-margin__bottom--large\">ラスト</h3>\n<h2 class=\"c-headingSecond u-margin__bottom--large\">香水のつける場所</h2>\n<!-- /wp:html -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->', 'テスト', '', 'inherit', 'closed', 'closed', '', '5-revision-v1', '', '', '2021-10-05 22:01:20', '2021-10-05 13:01:20', '', 5, 'http://localhost:8888/?p=8', 0, 'revision', '', 0),
